import React , { useState } from "react";
import { useQuery } from "@apollo/client";
import { GET_CHARACTERS } from "./graphql/queries.graphql";
import CharacterList from "./components/CharacterList";
import Filter from "./components/Filter";
import { useTranslation } from "react-i18next";


function App(){
    const { t, i18n } = useTranslation();

    const { filters, setFilters} = useState({status: '', species: ''});

    const { loading, error, data } = useQuery(GET_CHARACTERS,{
        variables: { status: filters.status, species: filters.species},
        });

        const handleFilterChange = (filterType, value) => {
            setFilters (prev => ({ ...prev, [filterType]: value}));
        };
        const filteredCharacters = data ? data.characters.results.filter((character) => {
            return(
                (!filters.status || character.status === filters.status) &&
                (!filters.species || character.status === filters.species)
            );
        }) : [];


    if(loading) return <p>{t('loading')}</p>;
    if(error) return <p>{t('error')}:{error.message}</p>

    return (
        <div className="App">
            <h1>{t('Rick and Morty Characters')} </h1>
            <Filter onFilterChange={handleFilterChange} />
            <CharacterList characters={filteredCharacters}/>
            <footer>
                <button onClick={() => i18n.changeLanguage('en')}>English</button>
                <button onClick={() => i18n.changeLanguage('de')}>Deutsch</button>
            </footer>

        </div>
    )
}
export default App;
