import React, { useState, useEffect } from "react";
import { useTranslation } from "react-i18next";
import CharacterList from "./components/CharacterList";
import CharacterFilter from './components/CharacterFilter'
// import '/.i18n';

function App() {
  // const { t, i18n } = useTranslation();
  const [characters, setCharacters] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filteredCharacters, setFilteredCharacters] = useState([])
  const [language, setLanguage] = useState('en')
  async function fetchCharacterData() {
    try {
      const res = await fetch("https://rickandmortyapi.com/api/character");
      const data = await res.json();
      return data.results || [];
    } catch (err) {
      console.error("Error fetching character data:", err);
      return [];
    }
  }

  useEffect(() => {
    const loadCharacters = async () => {
      const characterData = await fetchCharacterData();
      setCharacters(characterData);
      setFilteredCharacters(characterData);
      setLoading(false);
    };
    void loadCharacters();
  }, []);
  
  const handleFilterChange=({status, specie})=>{
    console.log("its calling ")
  const filteredCharacters = characters.filter((character)=>{
    const statusFiltered= status? character.status === status : true;
    const specieFiltered = specie? character.species === specie :true;
    return statusFiltered && specieFiltered
  })
  console.log("filteredCharacters", filteredCharacters)
  setFilteredCharacters(filteredCharacters);
  }
  return (
    <div className="App">
      <h1>Rick and Morty Characters</h1>
 
      <CharacterFilter onFilterChange={handleFilterChange} />
      {loading ? (
        <p>Loading...</p>
      ) : (
        <CharacterList characters={filteredCharacters} />
      )}
      <footer>
        <button /* onClick={() => i18n.changeLanguage("en")}*/>English</button>
        <button /*onClick={() => i18n.changeLanguage("de")}*/>Deutsch</button>
      </footer>
    </div>
  );
}

export default App;
